var self =this;
var options = {
  isAccessModeAlways: true
};
var result = kony.application.checkPermission(kony.os.RESOURCE_CAMERA, options);
if(result.canRequestPermission) {
  kony.application.requestPermission(kony.os.RESOURCE_CAMERA, function(res) {                
    if (res.status === kony.application.PERMISSION_DENIED) {
      alert("Please allow the Camera permissions in Settings App");
    }
    else if (res.status === kony.application.PERMISSION_GRANTED) {
      self.scan(eventobject);
    }
    else {
      alert("Camera permissions has been restricted.");
    }
  });
}
else if (result.status === kony.application.PERMISSION_DENIED) {      
  alert("Please allow the Camera permissions in Settings App");
} else if (result.status === kony.application.PERMISSION_GRANTED) {
  self.scan(eventobject);
}
else if (result.status === kony.application.PERMISSION_RESTRICTED){
